        // Add Marker
        const marker = window.L.marker([loc.lat, loc.lng]).addTo(mapInstance.current);

        // Add Service Radius Circle
        window.L.circle([loc.lat, loc.lng], {
          color: '#C5A059',
          fillColor: '#C5A059',
          fillOpacity: 0.1,
          radius: loc.radius
        }).addTo(mapInstance.current);

        marker.on('click', () => {
          mapInstance.current.setView([loc.lat, loc.lng], 11);
          setActiveArea(loc);
